        this.enable_edit_collections = _data_.enable_edit_collections;

        if(typeof _data_.enable_edit_theme == "undefined" || _data_.enable_edit_theme == ""){_data_.enable_edit_theme=false;}
        this.enable_edit_theme = _data_.enable_edit_theme;

        if(typeof _data_.default_theme == "undefined" || _data_.default_theme == ""){_data_.default_theme="Dark";}
        this.default_theme = _data_.default_theme;

        if(typeof _data_.enable_edit_sort == "undefined" || _data_.enable_edit_sort == ""){_data_.enable_edit_sort=false;}
        this.enable_edit_sort = _data_.enable_edit_sort;

